(function() {

  // --- Initiate Messenger --- //
  // Overriding alert with Messenger errors
  if (window.Messenger) {
    Messenger.options = {
      extraClasses: 'messenger-fixed messenger-on-top messenger-on-right',
      theme: 'flat'
    };
    window.alert = function(msg, type) {
      if (!type) type = 'error';
      if (!msg) msg = 'error';
      msg = msg.message || msg;
      if (!msg) return;
      if (window.Raven && type == 'error') {
        Raven.captureException(new Error(msg), {
          logger: 'alert',
          level: 'warning',
          checksum: msg.replace(/\d/g, '').slice(0,32) // To manually control the error aggregation
        });
      }

      Messenger().post({
        hideAfter: 20,
        message: msg,
        type: type,
        showCloseButton: true
      });
    };
  }

})();



